let alphabet = "abcdefghijklmnopqrstuvwxyz";

function toArr(text) {
  let array = text
    .split("(")
    .join("")
    .split(")")
    .join("")
    .split("\t")
    .join(" ")
    .split(/\r?\n/);

  //remove blurbs
  array = array.filter((line) => {
    return line.split(" ").length < 10 && line[0] && line[0] !== "—";
  });
  //remove parenthesis
  array = array.map((line) => {
    let split = line.split("[");
    return split[0];
  });

  //remove See's
  array = array.map((line) => {
    let split = line.split("See");
    return split[0];
  });

  // remove *'s
  array = array.map((line) => {
    return line.split("*").join("");
  });

  return array;
}

function allCapital(line) {
  if (line === line.toUpperCase()) {
    return true;
  } else {
    return false;
  }
}

function toObjects(arr) {
  let entries = [];
  let lastEntry = null;
  arr.map((line, index) => {
    if (
      allCapital(line) &&
      arr[index + 1] &&
      ((isInfo(arr[index + 1]) && !isInfo) ||
        findPrevLine(arr, index) ||
        alphabet.indexOf(arr[index].toLowerCase()[0]) >
          alphabet.indexOf(findNextNonInfo(arr, index).toLowerCase()[0]))
    ) {
      entries.push({
        name: line.trim().toLowerCase(),
        subIngredients: [],
        flavorAffinities: [],
        techniques: [],
      });
      lastEntry = line.trim().toLowerCase();
    } else if (lastEntry) {
      if (line.includes("+") && !isInfo(line)) {
        entries
          .find((entry) => entry.name === lastEntry)
          .flavorAffinities.push(line);
      } else if (line.includes("Techniques")) {
        line
          .split(":")
          .map((line) => line.split(","))
          .flat()
          .filter((line) => !line.includes("Techniques"))
          .forEach((technique) => {
            if (technique[0]) {
              entries
                .find((entry) => entry.name === lastEntry)
                .techniques.push(technique.trim().toLowerCase());
            }
          });
      } else if (
        !line.includes("Affinities") &&
        !isInfo(line) &&
        line[0] !== "-" &&
        !line.includes(".") &&
        !entries
          .find((entry) => entry.name === lastEntry)
          .subIngredients.includes(line.trim().toLowerCase())
      ) {
        entries
          .find((entry) => entry.name === lastEntry)
          .subIngredients.push(line.trim().toLowerCase());
      }
    }
  });
  return entries;
}

function isInfo(line) {
  if (
    line.includes("Season") ||
    line.includes("Weight") ||
    line.includes("Function") ||
    line.includes("Volume") ||
    line.includes("Techniques") ||
    line.includes("Taste") ||
    line.includes("Tips") ||
    line.includes("Botanical Relatives")
  ) {
    return true;
  } else {
    return false;
  }
}

function isWrappedInfo(arr, index) {
  if (isInfo(arr[index - 1] && isInfo(arr[index + 1]))) {
    return true;
  } else {
    return false;
  }
}

function findNextNonInfo(arr, index) {
  let start = index + 1;
  let nextLine = arr[start + 1];
  while (isInfo(arr[start])) {
    nextLine = arr[start + 1];
    start++;
  }
  return nextLine;
}

