function findPrevLine(arr, index) {
  if (arr[index - 1]) {
    if (
      alphabet.indexOf(arr[index].toLowerCase()[0]) <
      alphabet.indexOf(arr[index - 1].toLowerCase()[0])
    ) {
      return true;
    } else {
      return false;
    }
  } else {
    return true;
  }
}
function removeDuplicateFaTq(objects) {
  return objects.map((obj) => {
    return {
      ...obj,
      flavorAffinities: [...new Set(obj.flavorAffinities)],
      techniques: [...new Set(obj.techniques)],
    };
  });
}

function bookToData(book) {
  return removeDuplicateFaTq(toObjects(toArr(book))).filter(
    (obj) => obj.subIngredients.length
  );
}

